function drawTriangle(size) {
    if (size < 1) {
        console.log("Некорректно задан размер. Размер треугольника должен быть больше 1");
        return;
    }
    for (let i = 0; i < size; i++) {
        const triangleLine = [];
        for (let j = 0; j < i + 1; j++) {
            triangleLine.push("*");
        }
        console.log(triangleLine.join(""));
    }
}

function drawTriangleSym(size, symbol) {
    if (size < 1) {
        console.log("Некорректно задан размер. Размер треугольника должен быть больше 1");
        return;
    }
    for (let i = 0; i < size; i++) {
        const triangleLine = [];
        for (let j = 0; j < i + 1; j++) {
            triangleLine.push(`${symbol}`);
        }
        console.log(triangleLine.join(""));
    }
}

